    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
        String mobile=jTextField1.getText();
        String password=jPasswordField2.getText();
        
        try{
        Class.forName("com.mysql.jdbc.Driver");
        
        Connection con=(Connection) DriverManager.getConnection("jdbc:mysql://localhost/dream","root","root");
        
        Statement st=con.createStatement();
        
        ResultSet res =st.executeQuery("select * from user where mobile='"+mobile+"'");
       
            
           while(res.next()){
        if(password.equals(res.getString("password"))&& mobile.equals(res.getString("mobile"))){
            int uid=res.getInt("uid");
        new home(uid).setVisible(true);
        this.dispose();
        
        }
        else{
        
            JOptionPane.showMessageDialog(null,"Plz enter valid mobile or Password", "Error",  JOptionPane.WARNING_MESSAGE);
                              
        }
           }
        
        }catch(Exception e)
        {
            
        System.out.print(e);
        }
        
        jTextField1.setText("");
       jPasswordField2.setText("");
       
    }//GEN-LAST:event_jButton1ActionPerformed

