    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       String name=jTextField4.getText();
       String mobile=jTextField2.getText();
       String email=jTextField3.getText();
       
        String password=jPasswordField3.getText();
        String cpassword=jPasswordField4.getText();
        
       
            
            if(jTextField4.getText().isEmpty()==false&&jTextField2.getText().isEmpty()==false&&jTextField3.getText().isEmpty()==false&&jPasswordField3.getText().isEmpty()==false&&jPasswordField4.getText().isEmpty()==false){
                    
                     if(password.equals(cpassword)){
                try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection con=(Connection) DriverManager.getConnection("jdbc:mysql://localhost/dream","root","root");
       // System.out.println(con);
        Statement st=con.createStatement();
        st.executeUpdate("insert into user(name,mobile,email,password) values('"+name+"','"+mobile+"','"+email+"','"+password+"')");
        
        this.dispose();
         new signup().setVisible(true);
        
        }catch(Exception e)
        {
        System.out.print(e);
        }
        }
            else{
                         JOptionPane.showMessageDialog(null,"write same password", "Error",  JOptionPane.WARNING_MESSAGE);
            }
        
        }else{
        
            JOptionPane.showMessageDialog(null,"Plz fill all the feilds", "Error",  JOptionPane.WARNING_MESSAGE);
        }
        
    }//GEN-LAST:event_jButton3ActionPerformed

